// NAV OVERLAY JS
$(".nav-overlay-btn").on("click",function(e){
  e.preventDefault();
  var container = $("body");
  if((container).hasClass("nav-overlay-active")){
    $(container).removeClass("nav-overlay-active");
  }
  else{
    $(container).addClass("nav-overlay-active");
  }
});

$(".close-btn").on("click",function(e){
  e.preventDefault();
  var container = $("body");
  if((container).hasClass("nav-overlay-active")){
    $(container).removeClass("nav-overlay-active");
  }
});


// Script for sticky header OR add class on 1st scroll
$(window).scroll(function() {
    if ($(this).scrollTop() > 1){  
        $('#topBar').addClass("sticky");
    }
    else{
        $('#topBar').removeClass("sticky");
    }
});


// Page RoleBack to Top
$(document).ready(function(){
    $('.roleback').click(function(){
        $("html, body").animate({ scrollTop: 0 }, 800);
        return false;
    });
});


// Go Down
$(function() {
  $('.goDown').on('click', function(e) {
    e.preventDefault();
    $('html, body').animate({ scrollTop: $($(this).attr('href')).offset().top}, 500, 'linear');
  });
});


